import Layout from "@/components/layout";
import MarketCard from "@/components/market-card";
import { CATEGORIES } from "@/lib/mock-data";
import { Button } from "@/components/ui/button";
import { ArrowRight, Flame, Sparkles, FilterX, Loader2 } from "lucide-react";
import { useRoute, Link } from "wouter";
import { useQuery } from "@tanstack/react-query";
import { fetchMarkets, type Market } from "@/lib/api";

export default function Home() {
  const [match, params] = useRoute("/markets/:category");
  const categoryId = params?.category;

  const { data: markets = [], isLoading } = useQuery({
    queryKey: ["markets"],
    queryFn: fetchMarkets,
  });

  // Filter logic
  const filteredMarkets = categoryId 
    ? markets.filter((m: Market) => m.category.toLowerCase().replace(" ", "-") === categoryId || m.category.toLowerCase() === categoryId)
    : markets;

  const featuredMarket = filteredMarkets.find((m: Market) => m.isFeatured);
  const otherMarkets = filteredMarkets.filter((m: Market) => m !== featuredMarket);

  const categoryLabel = categoryId 
    ? CATEGORIES.find(c => c.id === categoryId)?.label 
    : "All Markets";

  if (isLoading) {
    return (
      <Layout>
        <div className="flex items-center justify-center py-20">
          <Loader2 className="w-8 h-8 animate-spin text-primary" />
        </div>
      </Layout>
    );
  }

  return (
    <Layout>
      {/* Category Header (if filtered) */}
      {categoryId && (
        <div className="mb-8 flex items-center justify-between animate-in fade-in slide-in-from-top-2">
          <h1 className="text-3xl font-display font-bold">{categoryLabel}</h1>
          <Link href="/">
             <Button variant="ghost" size="sm" className="gap-2">
               <FilterX className="w-4 h-4" /> Clear Filter
             </Button>
          </Link>
        </div>
      )}

      {/* Featured Section */}
      {featuredMarket && (
        <section className="mb-12 animate-in fade-in slide-in-from-bottom-4 duration-700">
          <div className="flex items-center gap-2 mb-6">
            <Sparkles className="w-5 h-5 text-primary" />
            <h2 className="text-xl font-display font-bold uppercase tracking-wide">Featured {categoryId ? categoryLabel : "Market"}</h2>
          </div>
          <MarketCard market={featuredMarket} featured />
        </section>
      )}

      {/* Trending / List Section */}
      <section className="animate-in fade-in slide-in-from-bottom-8 duration-700 delay-100">
        <div className="flex items-center justify-between mb-6">
          <div className="flex items-center gap-2">
            <Flame className="w-5 h-5 text-secondary" />
            <h2 className="text-xl font-display font-bold uppercase tracking-wide">
              {categoryId ? `More in ${categoryLabel}` : "Trending Now"}
            </h2>
          </div>
          {!categoryId && (
            <Button variant="link" className="text-muted-foreground hover:text-primary gap-1">
              View All <ArrowRight className="w-4 h-4" />
            </Button>
          )}
        </div>

        {otherMarkets.length > 0 ? (
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
            {otherMarkets.map((market) => (
              <MarketCard key={market.id} market={market} />
            ))}
          </div>
        ) : (
          <div className="text-center py-20 bg-muted/10 rounded-xl border border-dashed border-muted">
            <h3 className="text-lg font-medium text-muted-foreground">No markets found in this category.</h3>
            <Link href="/">
              <Button variant="link" className="mt-2">View all markets</Button>
            </Link>
          </div>
        )}
      </section>

      {/* Promo Banner - Only on Home */}
      {!categoryId && (
        <section className="mt-16 mb-8 rounded-2xl bg-gradient-to-r from-primary/10 via-secondary/10 to-primary/5 border border-primary/20 p-8 md:p-12 text-center relative overflow-hidden">
          <div className="absolute top-0 left-0 w-full h-full bg-[radial-gradient(circle_at_50%_50%,rgba(0,229,255,0.1),transparent_70%)]" />
          <div className="relative z-10 max-w-2xl mx-auto">
            <h2 className="text-3xl font-display font-bold mb-4">Trade on the Future</h2>
            <p className="text-muted-foreground mb-8 text-lg">
              Join the world's most transparent prediction market. Trade with USDC, earn rewards, and hedge against real-world events.
            </p>
            <Button size="lg" className="bg-primary text-primary-foreground hover:bg-primary/90 rounded-full px-8 font-semibold text-md shadow-[0_0_20px_rgba(0,229,255,0.3)] hover:shadow-[0_0_30px_rgba(0,229,255,0.5)] transition-all">
              Get Started Now
            </Button>
          </div>
        </section>
      )}
    </Layout>
  );
}

shared/schema.ts.content:
import { sql } from "drizzle-orm";
import { pgTable, text, varchar, serial, integer, decimal, timestamp, boolean } from "drizzle-orm/pg-core";
import { createInsertSchema } from "drizzle-zod";
import { z } from "zod";

// Users table
export const users = pgTable("users", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  walletAddress: text("wallet_address").unique(),
  username: text("username").unique(),
  balance: decimal("balance", { precision: 10, scale: 2 }).notNull().default("1000.00"),
  createdAt: timestamp("created_at").defaultNow().notNull(),
});

export const insertUserSchema = createInsertSchema(users).omit({ id: true, createdAt: true });
export type InsertUser = z.infer<typeof insertUserSchema>;
export type User = typeof users.$inferSelect;

// Markets table
export const markets = pgTable("markets", {
  id: serial("id").primaryKey(),
  title: text("title").notNull(),
  description: text("description"),
  image: text("image").notNull(),
  category: text("category").notNull(),
  endDate: text("end_date").notNull(),
  volume: decimal("volume", { precision: 12, scale: 2 }).notNull().default("0"),
  isFeatured: boolean("is_featured").default(false),
  isResolved: boolean("is_resolved").default(false),
  resolvedOutcomeId: integer("resolved_outcome_id"),
  createdAt: timestamp("created_at").defaultNow().notNull(),
});

export const insertMarketSchema = createInsertSchema(markets).omit({ id: true, createdAt: true });
export type InsertMarket = z.infer<typeof insertMarketSchema>;
export type Market = typeof markets.$inferSelect;

// Market Outcomes table
export const outcomes = pgTable("outcomes", {
  id: serial("id").primaryKey(),
  marketId: integer("market_id").notNull().references(() => markets.id, { onDelete: "cascade" }),
  label: text("label").notNull(),
  probability: decimal("probability", { precision: 5, scale: 2 }).notNull(),
  color: text("color").notNull(),
});

export const insertOutcomeSchema = createInsertSchema(outcomes).omit({ id: true });
export type InsertOutcome = z.infer<typeof insertOutcomeSchema>;
export type Outcome = typeof outcomes.$inferSelect;

// Positions table (user's holdings in specific outcomes)
export const positions = pgTable("positions", {
  id: serial("id").primaryKey(),
  userId: varchar("user_id").notNull().references(() => users.id, { onDelete: "cascade" }),
  marketId: integer("market_id").notNull().references(() => markets.id, { onDelete: "cascade" }),
  outcomeId: integer("outcome_id").notNull().references(() => outcomes.id, { onDelete: "cascade" }),
  shares: decimal("shares", { precision: 10, scale: 2 }).notNull(),
  avgPrice: decimal("avg_price", { precision: 5, scale: 4 }).notNull(),
  createdAt: timestamp("created_at").defaultNow().notNull(),
  updatedAt: timestamp("updated_at").defaultNow().notNull(),
});

export const insertPositionSchema = createInsertSchema(positions).omit({ id: true, createdAt: true, updatedAt: true });
export type InsertPosition = z.infer<typeof insertPositionSchema>;
export type Position = typeof positions.$inferSelect;

// Trades table (transaction history)
export const trades = pgTable("trades", {
  id: serial("id").primaryKey(),
  userId: varchar("user_id").notNull().references(() => users.id, { onDelete: "cascade" }),
  marketId: integer("market_id").notNull().references(() => markets.id, { onDelete: "cascade" }),
  outcomeId: integer("outcome_id").notNull().references(() => outcomes.id, { onDelete: "cascade" }),
  type: text("type").notNull(), // 'buy' or 'sell'
  shares: decimal("shares", { precision: 10, scale: 2 }).notNull(),
  price: decimal("price", { precision: 5, scale: 4 }).notNull(),
  totalAmount: decimal("total_amount", { precision: 10, scale: 2 }).notNull(),
  createdAt: timestamp("created_at").defaultNow().notNull(),
});

export const insertTradeSchema = createInsertSchema(trades).omit({ id: true, createdAt: true });
export type InsertTrade = z.infer<typeof insertTradeSchema>;
export type Trade = typeof trades.$inferSelect;

client/src/components/ui/badge.tsx.content:
import * as React from "react"
import { cva, type VariantProps } from "class-variance-authority"

import { cn } from "@/lib/utils"

const badgeVariants = cva(
  // @replit
  // Whitespace-nowrap: Badges should never wrap.
  "whitespace-nowrap inline-flex items-center rounded-md border px-2.5 py-0.5 text-xs font-semibold transition-colors focus:outline-none focus:ring-2 focus:ring-ring focus:ring-offset-2" +
  " hover-elevate ",
  {
    variants: {
      variant: {
        default:
          // @replit shadow-xs instead of shadow, no hover because we use hover-elevate
          "border-transparent bg-primary text-primary-foreground shadow-xs",
        secondary:
          // @replit no hover because we use hover-elevate
          "border-transparent bg-secondary text-secondary-foreground",
        destructive:
          // @replit shadow-xs instead of shadow, no hover because we use hover-elevate
          "border-transparent bg-destructive text-destructive-foreground shadow-xs",
          // @replit shadow-xs" - use badge outline variable
        outline: "text-foreground border [border-color:var(--badge-outline)]",
      },
    },
    defaultVariants: {
      variant: "default",
    },
  }
)

export interface BadgeProps
  extends React.HTMLAttributes<HTMLDivElement>,
    VariantProps<typeof badgeVariants> {}

function Badge({ className, variant, ...props }: BadgeProps) {
  return (
    <div className={cn(badgeVariants({ variant }), className)} {...props} />
  )
}

export { Badge, badgeVariants }

